import React , { useState } from 'react'
import './CSS/LoginSignup.css'


const LoginSignup = () => {

  const [state,setState] = useState("Login");
  const [formData, setFormData] = useState({
    username:"",
    password:"",
    email:""
  })

  const changeHandler = (e)=>{
    setFormData({...formData,[e.target.name]:e.target.value})
  }

  const login = async () =>{
    console.log("Login Function Executed", formData);
  }
  const signup = async () =>{
    console.log("Signup Function Executed", formData);
    let responseData;
    await fetch('http://localhost:4000/signup',{
      method:'POST',
      headers:{
        Accept:'application/form-data',
        'Content-Type':'application/json',
      },
      body: JSON.stringify(formData),
    }).then((response)=> response.json()).then((data)=>responseData=data)

    if(responseData.success){
      localStorage.setItem('')
    }
  }
  
  return (
    <div className='loginsignup'>
      <div className="loginsignup-container">
        <h1>{state}</h1>
        <div className="loginsignup-fields">
          {state==="Sign Up"?<input name='username' value={formData.username} onChange={changeHandler} type="text" placeholder='Seu Nome' />:<></>}
          <input name='email' value={formData.email} onChange={changeHandler} type="email" placeholder='Endereço de Email' />
          <input name='password' value={formData.password} onChange={changeHandler} type="password" placeholder='Senha' />
        </div>
        <button onClick={()=>{state==="Login"?login():signup()}}>Continue</button>
        {state==="Sign Up"
        ?<p className="loginsignup-login">Já tem uma conta? <span onClick={()=>{setState("Login")}}>Faça login</span></p>
      :<p className="loginsignup-login">Criar uma conta? <span onClick={()=>{setState("Sign Up")}}>Clique aqui</span></p>}  
        <div className="loginsignup-agree">
          <input type="checkbox" name='' id='' />
          <p>Li e concordo com os termos de uso e política de privacidade.</p>
        </div>
      </div>
    </div>
  )
}

export default LoginSignup
